const monthNames = [
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
  'July',
  'August',
  'September',
  'October',
  'November',
  'December',
]

const date = new Date()
const year = date.getFullYear()
const month = date.getMonth()

let currentDate = {
  month: month,
  year: year,
  text: `${monthNames[month]} '${year.toString().slice(-2)}`
}

items[0].json.currentDate = currentDate

return items

