parameters.jsCode:
// Loop over input items and modify the 'Code' field to add spaces between characters
for (const item of $input.all()) {
  const code = item.json.Code;

  const spacedCode = code.split('').join(' ');

  item.json.Code = spacedCode;
}

return $input.all();
