parameters.query:
=query GetTeamsAndProjects {
  teams(filter: {name: {contains: "{{ $json['Linear team name'] }}"}}) {
    nodes {
      id
      name
      members {
        nodes {
          id
          name
          email
        }
      }
      projects {
        nodes {
          id
          name
          description
        }
      }
    }
  }
}

