// Set the title and the assignee based on the first line of the item

let firstLine = $json[$json.type].text.reduce((s, o) => {
  return s + o.text.content
}, "")
console.log('firstLin', firstLine)
const regex = /^(\[[^\]]*\]\s)?(.+)$/;
const match = firstLine.match(regex);
console.log('match', match)

if (match) {
  // If the first part is not present, match[1] will be undefined
  item.json.assignee_fragment = match[1]?.slice(1, -2) || null;
  item.json.title = match[2];
} else {
  item.json.title = firstLine;
  item.json.assignee_fragment = null;
}

// Set the new title in Notion format
// $url will be set later, once we have it
const prefix_link = [
  {"text":{"content":"["}},
  {"text":{"content":"In Linear", "link":{"url": "$url"} }},
  {"text":{"content":"] "}}
]
item.json.new_content = {
  "rich_text": [...prefix_link, ...item.json.to_do.text]
}

// Find a matching assignee
const members = $('Fetch Linear team details').item.json.data.teams.nodes[0].members.nodes
console.log('people', members)
console.log('fragment', item.json.assignee_fragment)
const matching_people = members.filter(p => 
  p.name.toLowerCase().startsWith(item.json.assignee_fragment?.toLowerCase())
)
console.log('mpeople', matching_people)
if (matching_people.length > 0) {
  item.json.assignee = matching_people[0]
}

item.pairedItem = 0

return item

