function extractMarkdown(obj) {
  console.log('obj', obj.text)
  return obj.text.reduce((s, o) => {
    if(o.text?.link) {
      return s + '[' + o.text.content + '](' + o.text.link?.url + ')'
    }
    return s + o.text.content
  }, "")
}


const indent = "    "; // Four spaces
let parent_ids = [$input.all()[0].json.root_id]

for(item of $input.all()){

  // Generate the markdown

  if(item.json.type) {

    const type = item.json.type

    if(type == 'bulleted_list_item' || type == 'toggle') {
      item.json.markdown = '* ' + extractMarkdown(item.json[type])
    } else if(type == 'numbered_list_item') {
      item.json.markdown = '1. ' + extractMarkdown(item.json[type])
    } else if(type == 'to_do') {
      item.json.markdown = '+ [ ] ' + extractMarkdown(item.json[type])
    } else if(type == 'image') {
      item.json.markdown = '![image]('+item.json.image[item.json.image.type].url+')'
    } else if(type == 'video') {
      item.json.markdown = '[ðŸŽ¬ Video]('+$input.all()[0].json.page_url + '?pvs=4#' + item.json.id.replaceAll('-', '') +')'
    } else {
      item.json.markdown = extractMarkdown(item.json[type])
    }

    // Figure out how much to indent it
    // If parent ID is in list, remove everything after that ID
    // If parent ID is not in list, add it
    // If parent is the same, do nothing
    const parent_id_index = parent_ids.indexOf(item.json.parent_id);

    // Check if the value is found
    if (parent_id_index !== -1) {
      // Remove all elements after the first occurrence
      parent_ids.splice(parent_id_index + 1);
    } else {
      parent_ids.push(item.json.parent_id)
    }

    // Indent the markdown
    //if (type != "image") {
      item.json.markdown = indent.repeat(parent_ids.length - 1) + item.json.markdown
    //}
  }
}

// On returning, add in the root block content at the beginning
return [
  ...[
    {
      "json": {
        "markdown":
extractMarkdown($input.all()[0].json.root_content)
      },
      "pairedItem": 0
    }
  ],
  ...$input.all()
]

