const data = $input.all().map(item => item.json)
const slugCount = {};

return data.map(item => {
  let slug = item.property_slug;

  if (slugCount[slug]) {
    slugCount[slug] += 1;
    slug = `${slug}-${slugCount[slug]}`;
  } else {
    slugCount[slug] = 1;
  }

  item.property_slug = slug;
  return item;
});

const blocks = $input.all().map(item => item.json);

let newRichText = '';
let bulletedListItems = [];
let numberedListItems = [];

blocks.forEach(block => {
  if (block.type === 'bulleted_list_item') {
    bulletedListItems.push(block.html);
  } else if (block.type === 'numbered_list_item') {
    numberedListItems.push(block.html);
  } else {
    if (bulletedListItems.length > 0) {
      newRichText += `<ul>${bulletedListItems.join('')}</ul>`;
      bulletedListItems = [];
    }
    if (numberedListItems.length > 0) {
      newRichText += `<ol>${numberedListItems.join('')}</ol>`;
      numberedListItems = [];
    }
    newRichText += block.html;
  }
});

if (bulletedListItems.length > 0) {
  newRichText += `<ul>${bulletedListItems.join('')}</ul>`;
}
if (numberedListItems.length > 0) {
  newRichText += `<ol>${numberedListItems.join('')}</ol>`;
}

const output = [{ newRichText }];
return output;

const blocks = $input.all().map(item => item.json);
const output = [];

blocks.forEach(block => {
  let html = '';

  switch (block.type) {
    case 'heading_1':
      html = block.heading_1.text.map(item => item.text.content).join(' ');
      html = `<h1>${html}</h1>`;
      break;
    case 'heading_2':
      html = block.heading_2.text.map(item => item.text.content).join(' ');
      html = `<h2>${html}</h2>`;
      break;
    case 'heading_3':
      html = block.heading_3.text.map(item => item.text.content).join(' ');
      html = `<h3>${html}</h3>`;
      break;
    case 'paragraph':
      html = `<p>${block.paragraph.text.map(item => {
        let content = item.text.content.trim();
        if (item.annotations.bold) content = `<b>${content}</b>`;
        if (item.annotations.italic) content = `<i>${content}</i>`;
        if (item.text.link) content = `<a href="${item.text.link.url}">${content}</a>`;
        return content;
      }).join(' ') || '   '}</p>`; // the space inside the apostrophes is on purpose, otherwise Webflow will automatically delete the empty blocks
      break;
    case 'quote':
      html = block.quote.text.map(item => item.text.content).join(' ');
      html = `<blockquote>${html}</blockquote>`;
      break;
    case 'bulleted_list_item':
      html = block.bulleted_list_item.text.map(item => item.text.content).join(' ');
      html = `<li>${html}</li>`;
      break;
    case 'numbered_list_item':
      html = block.numbered_list_item.text.map(item => item.text.content).join(' ');
      html = `<li>${html}</li>`;
      break;
    case 'image':
      const caption = block.image.caption.map(item => item.text.content).join(' ');
      html = `<figure><img src="${block.image.file.url}" alt="${caption}" /><figcaption>${caption}</figcaption></figure>`;
      break;
    case 'code':
      const codeContent = block.code.text.map(item => item.text.content).join('\n')
      html = `<pre><code>${codeContent}</code></pre>`
      break
    default:
      html = block.content ? `<div>${block.content}</div>` : '';
  }

  if (html) {
    output.push({
      block_id: block.id,
      type: block.type,
      html
    });
  }
});

return output;
