const webhook = $('Airtable Webhook').first().json;
const schema = $('Get Prompt Fields').first().json;
const { payloads } = $input.first().json;
if (!payloads.length) return [];

const event = payloads[payloads.length - 1];
const baseId = webhook.body.base.id;
const tableId = Object.keys(event.changedTablesById)[0];
const table = event.changedTablesById[tableId];

return {
  baseId,
  tableId,
  event_type: getEventType(table),
  fieldId: getFieldId(table),
  field: getField(getFieldId(table)),
  rowId: getRecordId(table),
}

function getEventType(changedTableByIdObject) {
  if (changedTableByIdObject['createdFieldsById']) return 'field.created';
  if (changedTableByIdObject['changedFieldsById']) return 'field.updated'
  if (changedTableByIdObject['changedRecordsById']) return 'row.updated';
  return 'unknown';
}

function getFieldId(changedTableByIdObject) {
  const field = changedTableByIdObject.createdFieldsById
    || changedTableByIdObject.changedFieldsById
    || null;

  return field ? Object.keys(field)[0] : null;
}

function getField(id) {
  return schema.fields.find(field => field.id === id);
}

function getRecordId(changedTableByIdObject) {
  const record = changedTableByIdObject.changedRecordsById
    || null;

  return record ? Object.keys(record)[0] : null;
}

