function paragraph(content, annotations={}) {
  return {
    "object": "block",
    "type": "paragraph",
    "paragraph": {
      "rich_text": [
        {
          "type": "text",
          "text": {
            "content": content
          },
          "annotations": annotations
        }
      ]
    }
  };
}
function bulletPoint(content) {
  return {
    "object": "block",
    "type": "bulleted_list_item",
    "bulleted_list_item": {
      "rich_text": [
        {
          "type": "text",
          "text": {
            "content": content
          }
        }
      ]
    }
  };
}

// combine AI generated content
const content = Object.assign({}, $('Generate Actionable Task').item.json.output, $('Get Summary & Meta Data').item.json.output);

blocks = [];

// append task description
blocks.push(paragraph(content.description));

if (content.bulletpoints) {
  for (let bulletpoint of content.bulletpoints) {
    blocks.push(bulletPoint(bulletpoint));
  }
}

// append empty line
blocks.push(paragraph(""));

// append devider
blocks.push({
  "object": "block",
  "type": "divider",
  "divider": {}
});

// append summary & meta data
blocks.push(paragraph("Email summary:"));
blocks.push(paragraph(content.summary));
blocks.push(paragraph(""));
blocks.push(paragraph(content.meta.sender + "\n" + content.meta.subject + "\n" + content.meta.date, {"italic": true}));

// build final object
output = {
  "parent": {
    "database_id": $('Get Route by ID').item.json.NotionDatabase.match(/https:\/\/www\.notion\.so\/[a-zA-Z0-9-]+\/([a-zA-Z0-9]{32})/)[1]
  },
  "properties": {
    "Name": {
      "title": [
        {
          "text": {
            "content": content.title
          }
        }
      ]
    }
  },
  "children": blocks
};

return { json: output };
