/*
 * Filename: addFileName.js
 * Purpose: Add a file name to binary data in an n8n workflow using mail_object from input
 */

const mailObject = $input.first().json.output.mail_object;
const fileName = `${mailObject}.mp3`;

return items.map(item => {
  if (item.binary && item.binary.data) {
    item.binary.data.fileName = fileName;
  }
  return item;
});
