const htmlContent = $input.first().json.content;

const cleanText = htmlContent
  .replace(/<[^>]*>/g, '') // remove  tags
  .replace(/\s+/g, ' ')    // normalize spaces
  .replace(/&nbsp;/g, ' ') // decode common entity
  .trim();

return [
  {
    json: {
      clean_content: cleanText
    }
  }
];
