// Sort files by creation time (oldest first)
const sorted = items.sort((a, b) => 
  new Date(a.json.createdTime) - new Date(b.json.createdTime));

const seen = {};
for (const item of sorted) {
  const md5 = item.json.md5Checksum;

  // Failsafe: Skip if md5Checksum is missing or empty
  if (!md5) {
    item.json.isDuplicate = false; // Mark as not duplicate to avoid issues
    continue; // Skip to the next item
  }

  item.json.isDuplicate = md5 in seen;
  if (!item.json.isDuplicate) seen[md5] = true;
}
return items;

// Sort files by creation time (latest first)
const sorted = items.sort((a, b) => 
  new Date(b.json.createdTime) - new Date(a.json.createdTime));

const seen = {};
for (const item of sorted) {
  const md5 = item.json.md5Checksum;

  // Failsafe: Skip if md5Checksum is missing or empty
  if (!md5) {
    item.json.isDuplicate = false; // Mark as not duplicate to avoid issues
    continue; // Skip to the next item
  }

  if (md5 in seen) {
    item.json.isDuplicate = true;
  } else {
    item.json.isDuplicate = false;
    seen[md5] = true;
  }
}
return items;
