// Loop over input items
for (const item of $input.all()) {
    // Extract the YouTube video ID using a regular expression
    const Source = item.json.Source;
    const videoIdMatch = Source.match(/(?:v=|\/)([a-zA-Z0-9_-]{11})/);

    const videoId = videoIdMatch ? videoIdMatch[1] : null; // Extracted video ID or null if not found

    // Add the video ID to the JSON
    item.json.videoId = videoId;
}

// Return all items with the new videoId field
return $input.all();

// Initialize an empty string to hold the concatenated transcript
let Transcript = "";

// Safeguard against undefined paths and ensure required properties exist
if ($json.data && $json.data.transcripts) {
    // Loop through all transcript objects
    for (const key in $json.data.transcripts) {
        if ($json.data.transcripts[key].custom) {
            const customArray = $json.data.transcripts[key].custom;

            // Extract and append text from each transcript entry
            for (const item of customArray) {
                if (item.text) {
                    Transcript += item.text + " "; // Add a space between texts
                }
            }
        }
    }
}

// Return the combined transcript as a new field
return [
    {
        json: {
            Transcript: Transcript.trim(), // Trim to clean up extra spaces
        },
    },
];

