const data = $input.all();
const floatingObject = $("Prepare form submission for workflow").all()[0].json;

floatingObject.at_baseId = $("[AIRTABLE] Get Base Schema from list").params.base.value;
floatingObject.at_formIndexTableId = null;
floatingObject.at_currentFormTableId = null;

if(!floatingObject.at_baseId)
  return null; // no bases configured

for (let index = 0; index < data.length; index++) {
  if(data[index].json.name.toLowerCase() === "form index")
  {
    floatingObject.at_formIndexTableId = data[index].json.id;
    break;
  }
}

return floatingObject;

const floatingObject = $("Get Form Index Reference Table ID").all()[0].json;
floatingObject.at_currentFormTableId = $("[AIRTABLE] Create Webflow Form Table").all()[0].json.id;

if(!floatingObject.at_formIndexTableId){
  floatingObject.at_formIndexTableId = $("[AIRTABLE]  Create Index Reference Table").all()[0].json.id;
}

return floatingObject;

const floatingObject = $("Get Form Index Reference Table ID").all()[0].json;
const formIndexData = $("[AIRTABLE] Find Webflow Form Record In Form Index Reference Table").all()[0].json;

floatingObject.at_currentFormTableId = null;

if(Object.keys(formIndexData).length === 0){
  return floatingObject;
}

floatingObject.at_currentFormTableId = $("[AIRTABLE] Find Webflow Form Record In Form Index Reference Table").all()[0].json.TableId;

return floatingObject;
