const inputArray = items.map(item => item.json);

const output = [
  {
    nodes: [
      {
        parameters: {
          formFields: {
            values: [
              {
                fieldOptions: {
                  values: inputArray.map(entry => ({ option: entry.value }))
                }
              }
            ]
          }
        }
      }
    ]
  }
];

// Return the transformed output
return output.map(item => ({ json: item }));

