   (() => {
      const nodeIndex = $json.nodes.findIndex(
         node => node.parameters?.formFields?.values.some(
            value => value.fieldType === 'dropdown' && value.fieldOptions?.values
         )
      );

      if (nodeIndex === -1) return 'No matching node found';

      const valueIndex = $json.nodes[nodeIndex].parameters.formFields.values.findIndex(
         value => value.fieldType === 'dropdown' && value.fieldOptions?.values
      );

      if (valueIndex === -1) return `nodes[${nodeIndex}].parameters.formFields.values - No matching dropdown value found`;

      return `nodes[${nodeIndex}].parameters.formFields.values[${valueIndex}].fieldOptions.values`;
   })()
}}
