const outputText = $json.output;

// Parse JSON from ChatGPT response
const parsedOutput = JSON.parse(outputText);

// Return parsed object for next nodes
return parsedOutput;

// Get all items passed into this node as an array
const items = $input.all();

// If you always have at least two items:
const firstItem = items[0].json;
const secondItem = items[1].json;
const thirdItem = items[2].json;

// Overwrite the first item’s “content” with the second item’s “content”
firstItem.content = secondItem.content;
firstItem.iterationCount = thirdItem.iterationCount

// Return a single new item containing the merged result
return [
  {
    json: firstItem
  }
];

const outputText = $json.output;

// Parse JSON from ChatGPT response
const parsedOutput = JSON.parse(outputText);

// Return parsed object for next nodes
return parsedOutput;

const data = { ...$json };

// Clean out <think> block if present
data.content = $json.choices[0].message.content.replace(/<think>[\s\S]*?<\/think>/g, '').trim();

// Convert citations array to markdown link list
const citations = $json.citations
  .map((url, i) => `- [${i + 1}](${url})`)
  .join('\n');

data.content += `\n\n---\n\n### Sources\n${citations}`;

return data;

