try {
  // Check if input exists and has the expected structure
  const input = $input.all();
  if (!input || !input.length) {
    throw new Error('No input data received');
  }

  const firstItem = input[0];
  if (!firstItem || !firstItem.json || !firstItem.json.output || !firstItem.json.output.output) {
    throw new Error('Invalid input structure: missing required properties');
  }

  const output = firstItem.json.output.output;

  // Validate title exists
  if (!output.title) {
    throw new Error('Missing title in output');
  }

  // Validate content exists
  if (!output.content) {
    throw new Error('Missing content in output');
  }

  const title = output.title;
  const content = output.content.replace(/<h1>.*?<\/h1>/s, '').trim();

  // Validate final content is not empty after processing
  if (!content) {
    throw new Error('Content is empty after processing');
  }

  console.log('Successfully processed content');

  console.log(title)
  console.log(content)

  return { title, content };

} catch (error) {
  // Log the error for debugging
  console.error('Error processing content:', error.message);

  // Return a graceful failure object
  return {
    error: true,
    message: error.message,
    title: '',
    content: '',
    timestamp: new Date().toISOString()
  };
}
