// Input data (from $json.Topics)
const topicsString = $json.Topics;

// Split the string by newlines and trim whitespace
const topicsArray = topicsString.split('\n').map(topic => topic.trim());

// Create an array of items for each topic
const items = topicsArray.map(topic => {
  return { json: { Topic: topic } };
});

// Output the new array of items
return items;

// Get all input items
const inputItems = $input.all();

// Create a Map to store the most upvoted item for each URL
const uniqueItemsMap = new Map();

for (const item of inputItems) {
  const url = item.json.URL;

  // Skip items where URL contains "redd.it"
  if (url && url.includes("redd.it")) {
    continue;
  }

  const upvotes = parseInt(item.json.Upvotes, 10) || 0; // Ensure upvotes is a number

  if (!uniqueItemsMap.has(url)) {
    // Add the first occurrence of the URL
    uniqueItemsMap.set(url, item);
  } else {
    // Compare upvotes and keep the item with the most upvotes
    const existingItem = uniqueItemsMap.get(url);
    const existingUpvotes = parseInt(existingItem.json.Upvotes, 10) || 0;
    if (upvotes > existingUpvotes) {
      uniqueItemsMap.set(url, item);
    }
  }
}

// Extract all unique items
const uniqueItems = Array.from(uniqueItemsMap.values());

// Return each unique item as a separate output
return uniqueItems;

/**
 * n8n Code Node for filtering top 30 Reddit-style comments by score/ups
 * and ensuring replies are included in the comment tree.
 * Excludes deleted comments.
 */

// Get all input items
const inputItems = $input.all();
const commentsArray = inputItems.flatMap(item => item.json);

/**
 * Checks if a comment is deleted.
 * @param {Object} commentObj - The comment to check.
 * @returns {boolean} - True if the comment is deleted, false otherwise.
 */
function isDeletedComment(commentObj) {
  return commentObj.author === "[deleted]" && commentObj.body === "[removed]";
}

// Function to recursively flatten a comment and its replies
function flattenCommentTree(commentObj) {
  // Skip deleted comments
  if (isDeletedComment(commentObj)) {
    return null;
  }

  const { body, ups, score, replies, author } = commentObj;

  // Calculate score
  const finalScore = typeof ups === 'number' ? ups : (score || 0);

  // Process comment
  const flatComment = {
    body: body || '',
    score: finalScore,
    author: author || 'Unknown',
    replies: [],
  };

  // Process replies
  if (
    replies &&
    replies.data &&
    Array.isArray(replies.data.children)
  ) {
    flatComment.replies = replies.data.children
      .filter(child => child.kind === 't1' && child.data)
      .map(child => flattenCommentTree(child.data)) // Recursively flatten replies
      .filter(reply => reply !== null); // Filter out null replies (deleted comments)
  }

  return flatComment;
}

// Flatten all comments, preserving hierarchy
const allComments = commentsArray
  .map(flattenCommentTree)
  .filter(comment => comment !== null); // Filter out null comments (deleted comments)

// Flatten the hierarchy to a list for scoring and filtering
function flattenForScoring(tree) {
  const result = [];
  tree.forEach(comment => {
    result.push(comment); // Add current comment
    if (comment.replies && comment.replies.length > 0) {
      result.push(...flattenForScoring(comment.replies)); // Add replies recursively
    }
  });
  return result;
}

// Flatten the hierarchy and sort by score
const flatList = flattenForScoring(allComments);
flatList.sort((a, b) => b.score - a.score);

// Select the top 30 comments
const top30 = flatList.slice(0, 30);

// Rebuild the hierarchy from the top 30
function filterHierarchy(tree, allowedBodies) {
  return tree
    .filter(comment => allowedBodies.has(comment.body))
    .map(comment => ({
      ...comment,
      replies: filterHierarchy(comment.replies || [], allowedBodies), // Recurse for replies
    }));
}

const allowedBodies = new Set(top30.map(comment => comment.body));
const filteredHierarchy = filterHierarchy(allComments, allowedBodies);

// Return in n8n format
return [
  {
    json: {
      comments: filteredHierarchy,
    },
  },
];

/**
 * Convert comments data into Markdown format with accurate hierarchy visualization.
 * Excludes deleted comments.
 */

// Input data (replace this with your actual comments data)
const data = $input.all()[0].json.comments;

/**
 * Checks if a comment is deleted.
 * @param {Object} comment - The comment to check.
 * @returns {boolean} - True if the comment is deleted, false otherwise.
 */
function isDeletedComment(comment) {
  return comment.author === "[deleted]" && comment.body === "[removed]";
}

/**
 * Filters out deleted comments and their replies.
 * @param {Array} comments - Array of comments.
 * @returns {Array} - Filtered array of comments.
 */
function filterDeletedComments(comments) {
  if (!comments || !comments.length) return [];

  return comments
    .filter(comment => !isDeletedComment(comment))
    .map(comment => {
      if (comment.replies && comment.replies.length > 0) {
        comment.replies = filterDeletedComments(comment.replies);
      }
      return comment;
    });
}

/**
 * Recursive function to format comments and replies into Markdown.
 * @param {Array} comments - Array of comments.
 * @param {number} level - Current level of the comment hierarchy for indentation.
 * @returns {string} - Formatted Markdown string.
 */
function formatCommentsToMarkdown(comments, level = 0) {
  let markdown = '';
  const indent = '  '.repeat(level); // Indentation for replies

  for (const comment of comments) {
    // Format the main comment
    markdown += `${indent}- **Author**: ${comment.author}\n`;
    markdown += `${indent}  **Score**: ${comment.score}\n`;
    markdown += `${indent}  **Comment**:\n\n`;
    markdown += `${indent}    > ${comment.body.replace(/\n/g, `\n${indent}    > `)}\n\n`;

    // Process replies if they exist
    if (comment.replies && comment.replies.length > 0) {
      markdown += `${indent}  **Replies:**\n\n`;
      markdown += formatCommentsToMarkdown(comment.replies, level + 1);
    }
  }

  return markdown;
}

// Filter out deleted comments first
const filteredData = filterDeletedComments(data);

// Generate the Markdown
const markdownOutput = formatCommentsToMarkdown(filteredData);

// Return the Markdown as an output for n8n
return [
  {
    json: {
      markdown: markdownOutput,
    },
  },
];

