// Extract input data from n8n
const inputData = $json.data;

// Ensure input is valid
if (!inputData || typeof inputData !== 'string') {
    return [{ error: "Invalid input data" }];
}

// Split the data into lines
let lines = inputData.split("\n");

// Extract only JSON entries
let jsonEntries = lines
    .map(line => line.trim()) // Remove spaces
    .filter(line => line.startsWith('data: {')) // Keep valid JSON objects
    .map(line => line.replace('data: ', '')); // Remove the prefix

// Ensure there are entries
if (jsonEntries.length === 0) {
    return [{ error: "No valid JSON entries found" }];
}

// Get only the LAST entry
let lastEntry = jsonEntries[jsonEntries.length - 1];

try {
    // Parse the last entry as JSON
    let jsonObject = JSON.parse(lastEntry);

    // Extract title and content
    return [{
        title: jsonObject.title || "No Title",
        content: jsonObject.content || "No Content"
    }];
} catch (error) {
    return [{ error: "JSON parsing failed", raw: lastEntry }];
}
