let binaries = {}, binary_keys = [];

for (const [index, inputItem] of Object.entries($input.all())) {
  binaries[`data_${index}`] = inputItem.binary.data;
  binary_keys.push(`data_${index}`);
}

return [{
    json: {
        binary_keys: binary_keys.join(',')
    },
    binary: binaries
}];
