// Get the input data from n8n
const inputData = items.map(item => item.json);

// Function to count words in a string
function countWords(text) {
    return text.trim().split(/\s+/).length;
}

// Function to count sentences in a string
function countSentences(text) {
    const sentences = text.match(/[^.!?]+[.!?]+/g) || [];
    return sentences.length;
}

// Function to calculate average sentence length
function averageSentenceLength(text) {
    const sentences = text.match(/[^.!?]+[.!?]+/g) || [];
    const sentenceLengths = sentences.map(sentence => sentence.trim().split(/\s+/).length);
    const totalWords = sentenceLengths.reduce((acc, val) => acc + val, 0);
    return sentenceLengths.length ? (totalWords / sentenceLengths.length) : 0;
}

// Function to calculate average word length
function averageWordLength(text) {
    const words = text.trim().split(/\s+/);
    const totalCharacters = words.reduce((acc, word) => acc + word.length, 0);
    return words.length ? (totalCharacters / words.length) : 0;
}

// Function to calculate Flesch-Kincaid Readability Score
function fleschKincaidReadability(text) {
    // Split text into sentences (approximate)
    const sentences = text.match(/[^.!?]+[.!?]*[\n]*/g) || [];
    // Split text into words
    const words = text.trim().split(/\s+/);
    // Estimate syllable count by matching vowel groups
    const syllableCount = (text.toLowerCase().match(/[aeiouy]{1,2}/g) || []).length;

    const sentenceCount = sentences.length;
    const wordCount = words.length;

    // Avoid division by zero
    if (wordCount === 0 || sentenceCount === 0) return 0;

    const averageWordsPerSentence = wordCount / sentenceCount;
    const averageSyllablesPerWord = syllableCount / wordCount;

    // Flesch-Kincaid formula
    return 206.835 - (1.015 * averageWordsPerSentence) - (84.6 * averageSyllablesPerWord);
}


// Prepare the result array for n8n output
const resultArray = [];

// Loop through the input data and analyze each LLM response
inputData.forEach(item => {
    const llmResponse = item.llm_response;

    // Perform the analyses
    const wordCount = countWords(llmResponse);
    const sentenceCount = countSentences(llmResponse);
    const avgSentenceLength = averageSentenceLength(llmResponse);
    const readabilityScore = fleschKincaidReadability(llmResponse);
    const avgWordLength = averageWordLength(llmResponse);

    // Structure the output to include original input and new calculated values
    resultArray.push({
        json: {
            llm_response: item.llm_response,
            prompt: item.prompt,
            model: item.model,
            start_time: item.start_time,
            end_time: item.end_time,
            time_diff: item.time_diff,
            word_count: wordCount,
            sentence_count: sentenceCount,
            average_sent_length: avgSentenceLength,
            readability_score: readabilityScore,
            average_word_length: avgWordLength
        }
    });
});

// Return the result array to n8n
return resultArray;
