var item = $json;

// Check first if is error
if (item.error) {
  if ("The resource you are requesting could not be found" == item.error) {
    item["status"] = "new";
  } else {
    item["status"] = "error";
  }
  return $input.item;
}

// If not error check file with saved version
var workflowFromN8n = item["workflow-from-n8n"];
var workflowFromGitlab = item["workflow-from-gitlab"];
var areEquals = objectsAreEquals(workflowFromN8n, workflowFromGitlab);

if (areEquals) {
  item["status"] = "same";
} else {
  item["status"] = "diff";
}

// Return Item
return item;

/**
 * Compare to objects
 * @param object1 
 * @param object2 
 * @returns true if the are the same without ignored fields
 */
function objectsAreEquals(object1, object2) {
  const objectKeys1 = Object.keys(object1);
  const objectKeys2 = Object.keys(object2);

  // If the numbers of fields are differents, the objects are differents
  if (objectKeys1.length !== objectKeys2.length) {
    return false;
  }
  for (const key of objectKeys1) {
    // Define some fields to be ignored
    var ignoreCurrent = false;
    switch (key) {
      case "updatedAt": // Changed because workflow change... not usefull
      case "global": // changed for running reasons, no need to check
        ignoreCurrent = true;
    }

    // If it's not an ignored field
    if (!ignoreCurrent) {
      const value1 = object1[key];
      const value2 = object2[key];
      const isBothAreObjects = isObject(value1) && isObject(value2);

      // If it's objects recursive check
      if (isBothAreObjects && !objectsAreEquals(value1, value2)) {
        return false;
      }

      // If it's not objects, just compare values
      if (!isBothAreObjects && value1 != value2) {
        return false;
      }
    }
  }
  return true;
}

/**
 * Tool function to determine if an parameter is an object
 * @param object 
 * @returns 
 */
function isObject(object) {
  return object !== null && typeof object === "object";
}
