            // Check if row is valid and its properties exist
            const isValid = row 
                && row.dimensionValues 
                && row.dimensionValues[0] 
                && row.dimensionValues[0].value 
                && row.metricValues 
                && row.metricValues.length > 0;

            if (!isValid) {
                console.log('Ignoring invalid or null row:', row);
