// Convert the HTML string to a Buffer
const buffer = Buffer.from($json.body, 'utf-8');

// Return the buffer as binary data
return [
  {
    binary: {
      data: {
        data: buffer.toString('base64'), // Convert buffer to base64 string
        mimeType: 'text/html',
        fileName: 'sample.html'
      }
    }
  }
];
