parameters.inputSchema:
{
 "type": "object",
 "properties": {
 "video_id": {
 "type": "string",
 "description": "The ID of the video to fetch details for"
 }
 },
 "required": ["video_id"]
}

{
 "type": "object",
 "properties": {
 "channel_id": {
 "type": "string",
 "description": "The ID of the channel to fetch videos from"
 },
 "number_of_videos": {
 "type": "integer",
 "description": "The maximum number of videos to retrieve (max 50)"
 },
 "order": {
 "type": "string",
 "enum": ["date", "relevance", "viewCount"],
 "description": "Order in which to fetch videos"
 },
 "publishedAfter": {
 "type": "string",
 "description": "Timestamp for filtering like 2023-11-03T15:28:05Z."
 }
 },
 "required": ["channel_id", "number_of_videos", "order"]
}

{
 "type": "object",
 "properties": {
 "video_id": {
 "type": "string",
 "description": "The ID of the video to fetch comments from"
 }
 },
 "required": ["video_id"]
}

{
 "type": "object",
 "properties": {
 "type": {
 "type": "string",
 "enum": ["video", "channel"],
 "description": "Type of results to retrieve: video or channel"
 },
 "query": {
 "type": "string",
 "description": "Search query. Supports | for OR and - to exclude terms"
 },
 "sorting": {
 "type": "string",
 "enum": ["date", "viewCount", "relevance"],
 "description": "Sorting criteria for search results"
 },
 "publishedAfter": {
 "type": "string",
 "description": "Timestamp for filtering like 2023-11-03T15:28:05Z"
 }
 },
 "required": ["type", "query", "sorting"]
}

{
 "type": "object",
 "properties": {
 "url": {
 "type": "string",
 "description": "URL of the thumbnail image to analyze"
 },
 "prompt": {
 "type": "string",
 "description": "Customizable instruction to guide the image analysis"
 }
 },
 "required": ["url", "prompt"]
}
