// Merge all incoming items (each with one binary photoX) into one item
const mergedItem = {
  json: {},
  binary: {}
};

for (const item of items) {
  // Copy every binary field from each item into mergedItem.binary
  for (const [key, bin] of Object.entries(item.binary || {})) {
    mergedItem.binary[key] = bin;
  }
}

// Return a single-item array
return [mergedItem];
