/**
 * Basic function to get specific data (title, description, duration)
 * from pre-fetched HTML body data of a YouTube video page.
 * Assumes the HTML body is passed as the first argument.
 *
 * @param {string} body HTML body data of the YouTube video page.
 * @param {string} video_id YouTube video ID.
 * @param {string} url YouTube video URL.
 * @returns {Promise<{title: string, description: string, duration: number}>} Video Basic Info.
 * @throws {Error} If video ID cannot be extracted, captcha is detected,
 * or necessary data cannot be parsed.
 */
async function video_basic_info(body, video_id, url) {
  // --- Input Validation ---
  if (typeof body !== 'string') {
    throw new Error('body parameter must be a string of HTML');
  }
  if (typeof video_id !== 'string' || !video_id.trim()) {
    throw new Error('video_id parameter must be a non-empty string');
  }
  if (typeof url !== 'string' || !url.trim()) {
    throw new Error('url parameter must be a non-empty URL string');
  }

  // --- Captcha Check ---
  // Added check for consent page as well
  if (body.includes('Our systems have detected unusual traffic') || body.includes('consent.google.com')) {
    throw new Error('Captcha or Consent page encountered: YouTube likely requires interaction or detected bot-like activity.');
  }

  // --- Extract Player Data ---
  let player_data;
  try {
    // More robust regex to find ytInitialPlayerResponse, stopping at the next semicolon
    const player_data_match = body.match(/var ytInitialPlayerResponse\s*=\s*({.+?});\s*(?:var |<\/script)/);
    if (!player_data_match || !player_data_match[1]) {
      // Fallback attempt with simpler split (less reliable)
      const split_data = body.split('var ytInitialPlayerResponse = ');
      player_data = split_data?.[1]?.split(';</script>')?.[0];
    } else {
      player_data = player_data_match[1];
    }

    if (!player_data) {
      // Check for common failure indicators in the HTML if data isn't found
      if (body.includes('<title>YouTube</title>') && !body.includes('videoDetails')) {
        throw new Error('Could not find ytInitialPlayerResponse data. The page might be a generic YouTube page, not a video page, or the structure changed.');
      }
      throw new Error('Could not find ytInitialPlayerResponse data.');
    }
  } catch (error) {
    console.log("Error during player_data extraction:", error);
    throw new Error(`Failed during player data extraction: ${error}`);
  }


  let player_response;
  try {
    player_response = JSON.parse(player_data);
  } catch (e) {
    console.log("Raw player_data that failed parsing:", player_data.substring(0, 500) + '...'); // Log start of data
    throw new Error(`Failed to parse ytInitialPlayerResponse JSON: ${e}`);
  }

  // --- Extract Required Video Details ---
  // Use optional chaining for safety
  const vid = player_response?.videoDetails;

  // Add more robust checking, including playability status
  if (!vid) {
    const playabilityStatus = player_response?.playabilityStatus;
    let reason = "videoDetails object not found in the response.";
    if (playabilityStatus?.status && playabilityStatus.status !== 'OK') {
      reason = ` Playability status: ${playabilityStatus.status}. Reason: ${playabilityStatus.reason ||
        playabilityStatus.errorScreen?.playerErrorMessageRenderer?.reason?.simpleText ||
        playabilityStatus.errorScreen?.playerKavRenderer?.reason?.simpleText ||
        'No specific reason provided.'
        }`;
    } else if (playabilityStatus?.status === 'OK' && !vid) {
      reason = " Playability status is OK, but videoDetails is still missing. Response structure might have changed.";
    }
    throw new Error(`Could not get video details. ${reason}`);
  }

  // --- Return Simplified Data ---
  // Ensure values exist before accessing, provide defaults if necessary
  return {
    title: vid.title || 'N/A',
    description: vid.shortDescription || '', // Default to empty string if missing
    duration: Number(vid.lengthSeconds) || 0, // Default to 0 if missing/invalid
  };
}


return video_basic_info($input.first().json.data, $('Update Context Intent').first().json.output.id, $('Update Context Intent').first().json.output.url);

