function generateRichTextBlock(title, items) {
  return {
    type: "rich_text",
    elements: [
      {
        type: "rich_text_section",
        elements: [{ type: "text", text: `${title}:` }]
      },
      {
        type: "rich_text_list",
        style: "bullet",
        indent: 0,
        border: 0,
        elements: items.map(item => ({
          type: "rich_text_section",
          elements: [{ type: "text", text: item }]
        }))
      }
    ]
  };
}

function generateRichText(value, metadata) {
  if (!value || typeof value !== 'object') return [];

  const { name, link, title, formattedDate } = metadata;

  const baseBlocks = [
    {
      type: "header",
      text: {
        type: "plain_text",
        text: name
      }
    },
    {
      type: "context",
      elements: [{
        text: `${formattedDate}  |  <${link}|${title}>`,
        type: "mrkdwn"
      }]
    },
    { type: "divider" }
  ];

  const sections = [
    { key: "features", title: "Features" },
    { key: "fixes", title: "Fixes" },
    { key: "others", title: "Others" }
  ];

  const contentBlocks = sections
    .filter(({ key }) => Array.isArray(value[key]) && value[key].length > 0)
    .map(({ key, title }) => generateRichTextBlock(title, value[key]));

  return {
    blocks: [...baseBlocks, ...contentBlocks]
  };
}

function processAllItems(infoExtractor, ifNotEmpty, dateFormat) {
  return infoExtractor.all().map((item, index) => {
    const metadata = {
      name: ifNotEmpty.all()[index].json.name,
      link: ifNotEmpty.all()[index].json.link,
      title: ifNotEmpty.all()[index].json.title,
      formattedDate: dateFormat.all()[index].json.formattedDate
    };
    return generateRichText(item.json.output, metadata);
  });
}

return processAllItems(
  $('Information Extractor'),
  $('If Not Empty'), 
  $('Date Format')
);
