// Method 2: n8n workflow compatible version
return $input.all().map(item => {
  return {
    image_url: item.json.image_url
  };
});

const chunks = $input.first().json.data.split('\n\n');

let imageUrl = null;

// 反向遍历 chunks (从最新数据开始检查)
for (let i = chunks.length - 1; i >= 0; i--) {
    const chunk = chunks[i];

    if (!chunk.startsWith('data: ')) continue;

    try {
        const jsonStr = chunk.substring(6); // 去掉 "data: " 前缀
        if (jsonStr.trim() === '[DONE]') continue;

        const data = JSON.parse(jsonStr);

        // 检查是否包含图片标记（Markdown 图片语法）
        if (data.choices && data.choices[0].delta.content) {
            const content = data.choices[0].delta.content;
            const urlMatch = content.match(/!\[.*?\]\((https?:\/\/[^\s]+)\)/);

            if (urlMatch && urlMatch[1]) {
                imageUrl = urlMatch[1];
                break;
            }
        }
    } catch (e) {
        continue;
    }
}

return {
    image_url: imageUrl,
    finish_reason: imageUrl ? "success" : "not_found"
};
