// Create our Slack message
// This will output a list of RSS items in the following format
// Title - Description
let message = "*:new: Posts from yesterday :new:*\n\n";

// Loop the input items
for (item of items) {
  message += "*<" + item.json.link + "|" + item.json.title + ">*\n" + item.json.contentSnippet + "\n\n"; 
}

// Return our message
return [{json: {message}}];
