parameters.query:
query ($filter: IssueFilter) {
  issues(filter: $filter, first: 100) {
    pageInfo {
      hasNextPage
      endCursor
    }
    nodes {
      id
      identifier
      url
      title
      priorityLabel
      createdAt
      completedAt
      state {
        type
        name
      }
      cycle {
        number
      }
      estimate
      labels { nodes { name } }
    }
  }
}

=query ($filter: IssueFilter) {
  issues(filter: $filter, first: 100, after: "{{ $json.after }}") {
    nodes {
      id
      identifier
      url
      title
      priorityLabel
      createdAt
      completedAt
      state {
        type
        name
      }
      cycle {
        number
      }
      estimate
      labels { nodes { name } }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

