parameters.jsonBody:
={
  "data": {
    "status": {
      "name": "{{ $json["productboard_status"] }}"
    },
    "timeframe": {
      {{ $json["targetDate"] ? '"granularity": "month",': '"granularity": "none",'}}
      {{ $json["targetDate"] ? '"startDate": "' + $json['targetDate'].substring(0, 7) + '-01' +'",': '"startDate": "none",'}}
      {{ $json["targetDate"] 
        ? (() => {
            const date = new Date($json['targetDate']);
            const year = date.getFullYear();
            const month = date.getMonth() + 1;
            const lastDay = new Date(year, month, 0).getDate();
            return `"endDate": "${year}-${month.toString().padStart(2, '0')}-${lastDay}"`;
          })() 
        : '"endDate": "none"'}}
    }
  }
}

parameters.jsCode:
const linearStatus = $json.linear_status;
let productboardStatus;

switch(linearStatus) {
  case 'Backlog':
    productboardStatus = 'Candidate';
    break;
  case 'Planned':
    productboardStatus = 'Planned';
    break;
  case 'Paused':
    productboardStatus = 'Planned';
    break;
  case 'In Progress':
    productboardStatus = 'In progress';
    break;
  case 'Completed':
    productboardStatus = 'Released';
    break;
  case 'Canceled':
    productboardStatus = 'Won\'t do';
    break;
  default:
    productboardStatus = 'Candidate'; // Default or handle unknown status
}

return { productboard_status: productboardStatus };

