// Get the input text
const text = $input.item.json.text;

// Ensure text is not null or undefined
if (!text) {
  throw new Error('Input text is empty');
}

// Function to split text into sentences while preserving dates and list items
function splitIntoSentences(text) {
  const monthNames = '(?:Januar|Februar|März|April|Mai|Juni|Juli|August|September|Oktober|November|Dezember)';
  const datePattern = `(?:\\d{1,2}\\.\\s*(?:${monthNames}|\\d{1,2}\\.)\\s*\\d{2,4})`;

  // Split by sentence-ending punctuation, but not within dates or list items
  const regex = new RegExp(`(?<=[.!?])\\s+(?=[A-ZÄÖÜ]|$)(?!${datePattern}|\\s*[-•]\\s)`, 'g');

  return text.split(regex)
    .map(sentence => sentence.trim())
    .filter(sentence => sentence !== '');
}

// Split the text into sentences
const sentences = splitIntoSentences(text);

// Output a single object with an array of sentences
return { json: { sentences: sentences } };

