={{ 
  (() => {
    // List of known free email domains
    const freeEmailDomains = [
      'gmail.com',
      'yahoo.com',
      'hotmail.com',
      'outlook.com',
      'aol.com',
      'icloud.com',
      'mail.com',
      'yandex.com',
      'protonmail.com'
    ];

    // Extract email addresses using JMESPath
    const emailAddresses = $jmespath($json.gongData[0].parties, '[?affiliation==`External` || affiliation==`Unknown`].emailAddress');

    // Function to extract the domain from an email address
    const extractDomain = (email) => email.match(/@([\w.-]+)/)?.[1];

    // Filter out free email domains
    const companyDomains = emailAddresses
      .map(extractDomain)
      .filter(domain => domain && !freeEmailDomains.includes(domain.toLowerCase()));

    // Return the first non-free domain or "Unknown" if none are found
    return companyDomains[0] || 'Unknown';
  })()
}}

