// Input: items[] - each one is an article

// Step 1: Remove duplicates by link
const uniqueMap = new Map();
for (const item of items) {
  const link = item.json.link;
  if (!uniqueMap.has(link)) {
    uniqueMap.set(link, item.json);
  }
}

// Step 2: Sort by publication date from newest to oldest
const uniqueArticles = Array.from(uniqueMap.values());
uniqueArticles.sort((a, b) => b.pubDate - a.pubDate);

// Step 3: Take the top 50 most recent articles
const top20 = uniqueArticles.slice(0, 50);

// Step 4: Build clean, readable, efficient text
const formatted = top20.map((article, index) => {
  const title = article.title?.replace(/\(\)$/, '').trim() || 'No Title';
  const link = article.link || '';
  return `${index + 1}. ${title}\n${link}`;
});

return [
  {
    json: {
      chatgpt_input: formatted.join('\n\n') // Paragraphs separated by newlines
    }
  }
];
