// Extract all items from the input
const items = $input.all();

// Initialize Sets to store unique company domains and email addresses
const uniqueCompanies = new Set();
const uniqueEmails = new Set();

// Iterate through each item to populate the Sets
for (const item of items) {
  const domain = item.json['Company Domain'];
  const email = item.json['Email Address'];

  if (domain) uniqueCompanies.add(domain);
  if (email) uniqueEmails.add(email);
}

// Create the summary message
const message = `This week we discovered ${uniqueEmails.size} decision makers from ${uniqueCompanies.size} companies.`;

// Return the message as output
return [
  {
    json: {
      message,
      companies: uniqueCompanies.size,
      contacts: uniqueEmails.size,
    },
  },
];

