parameters.query:
=query yersterdaysOrders {
  orders(query: "created_at:{{$today.minus({days: 1})}}", first: 100) {
    edges {
      node {
        id
        name
        totalReceived
        customerJourneySummary {
          firstVisit {
            id
            source
            referrerUrl
            landingPage
            utmParameters {
              campaign
              content
              medium
              source
              term
            }
          }
        }
      }
    }
  }
}
