function convertToHTML(list) {
    let html = '';

    list.forEach((education, index) => {
        if (index > 0) {
            html += '<br /><br />'; // Add a new line if it's not the first item
        }
        html += `<b>Institution:</b> ${education.institution}<br />
<b>Start year:</b> ${education.start_year}<br />
<b>Degree:</b> ${education.degree}`;
    });

    return html;
}

// Assuming payload is already defined
const payload = $input.item.json.education;

const htmlOutput = convertToHTML(payload);
return {
    htmlOutput
};

function convertToHTML(list) {
    let html = '';

    list.forEach((item, index) => {
        if (index > 0) {
            html += '<br />'; // Add a new line if it's not the first item
        }
        html += `<b>Position:</b> ${item.position}
<b>Company:</b> ${item.company}
<br />
<b>Duration:</b> ${item.duration}
<br />
<b>Responsibilities:</b>
`;

        item.responsibilities.forEach((responsibility, i) => {
            html += `- ${responsibility}`;
            if (i < item.responsibilities.length - 1 || index < list.length - 1) {
                html += '<br />'; // Add new line if it's not the last responsibility in the last item
            }
        });
    });

    return html;
}

// Assuming payload is already defined
const payload = $input.item.json.employment_history;

const htmlOutput = convertToHTML(payload);
return {
    htmlOutput
};

