for (const item of $input.all()) {
  const text = item.json.text || '';
  const match = text.match(/\/resume\s+(.*)/);

  if (match) {
    item.json.resume = match[1].trim();
  } else {
    item.json.resume = null; 
  }
}

return $input.all();

