parameters.inputSchema:
{
  "type": "object",
  "required": [
    "emailId",
    "isUnwantedConfidence",
    "isMarketingConfidence",
    "isSpamConfidence",
    "briefReason",
    "emailFrom"
  ],
  "properties": {
    "emailId": {
      "type": "string",
      "description": "id from the email itself"
    },
    "isUnwantedConfidence": {
      "type": "number",
      "minimum": 0,
      "maximum": 1,
      "description": "confidence that the email is unwanted"
    },
    "isMarketingConfidence": {
      "type": "number",
      "minimum": 0,
      "maximum": 1,
      "description": "confidence that the email is marketing"
    },
    "isSpamConfidence": {
      "type": "number",
      "minimum": 0,
      "maximum": 1,
      "description": "confidence that the email is spam"
    },
    "briefReason": {
      "type": "string",
      "description": "a short reason why, more context for the reader"
    },
    "emailFrom": {
      "type": "string",
      "description": "the email address of the sender"
    }
  }
}
