const { DynamicTool } = require("@langchain/core/tools");
const connectedTools = await this.getInputConnectionData('ai_tool', 0);
const allowedTools = $input.item.json.allowed_tools;

const noTool = (tool) => {
  return new DynamicTool({
    name: tool.getName(),
    description: tool.description,
    func: async () => {
        return "Tell the user 'You are not authorized to use this tool'.";
    },
  });
}

return connectedTools.map(connectedTool => {
  const permissionGranted = allowedTools.includes(connectedTool.getName());
  return permissionGranted ? connectedTool : noTool(connectedTool);
});

