const extractYoutubeId = (url) => {
 // Regex pattern that matches both youtu.be and youtube.com URLs
 const pattern = /(?:youtube\.com\/(?:[^\/]+\/.+\/|(?:v|e(?:mbed)?)\/|.*[?&]v=)|youtu\.be\/)([^"&?\/\s]{11})/;
 const match = url.match(pattern);
 return match ? match[1] : null;
};

