import json

## Load & access the existing JSON data
for item in _input.all():
  data = item.json 

  # Define the output data structure
  output_data = {"data": [], "siren": True}

  # Unpack data from the single element list
  first_names = data["first_name"]
  last_names = data["last_name"]
  domain = data["domain"]
  full_name = data["full_name"]

    # Combine data into a list of dictionaries
  transformed_data = []
  for i, (first_name, last_name, domain_name, full_name_value) in enumerate(zip(first_names, last_names, domain, full_name)):
    transformed_data.append({
      "first_name": first_name,
      "last_name": last_name,
      "website": domain_name,
      "custom_fields": {
        "full_name": full_name_value}
    })

  output_data["data"] = transformed_data

  return output_data
