=query getProducts($first: Int = {{ $json.batchsize }}, $after: String = "{{ $json.endCursor }}") {
  products(first: $first, after: $after) {
    edges {
      node {
        title
        tags
        description
        variants(first: 1) {
          edges {
            node {
              price
            }
          }
        }
      }
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

