// Variables from a separate edit node
const baseUrl = $node["Manually Enter Other Data for Podcast Feed"].data.baseUrl; 
const podcastTitle = $node["Manually Enter Other Data for Podcast Feed"].data.podcastTitle;
const podcastDescription = $node["Manually Enter Other Data for Podcast Feed"].data.podcastDescription;
const authorName = $node["Manually Enter Other Data for Podcast Feed"].data.authorName;
const ownerName = $node["Manually Enter Other Data for Podcast Feed"].data.ownerName;
const ownerEmail = $node["Manually Enter Other Data for Podcast Feed"].data.ownerEmail;
const coverImageUrl = $node["Manually Enter Other Data for Podcast Feed"].data.coverImageUrl;
const language = $node["Manually Enter Other Data for Podcast Feed"].data.language || 'en-us';
const explicitContent = $node["Manually Enter Other Data for Podcast Feed"].data.explicitContent || false;
const itunesCategory = $node["Manually Enter Other Data for Podcast Feed"].data.itunesCategory;
const webhookUrl = $node["Webhook GET Podcast Feed"].data.webhookUrl

// Get the input items
const inputItems = items;

// Function to format date to RFC 822 format
function formatDate(dateString) {
 return new Date(dateString || new Date()).toUTCString();
}

// Function to convert duration from seconds to HH:MM:SS
function formatDuration(seconds = 0) {
 const hours = Math.floor(seconds / 3600);
 const minutes = Math.floor((seconds % 3600) / 60);
 const remainingSeconds = Math.floor(seconds % 60);

 const minutesStr = minutes.toString().padStart(2, '0');
 const secondsStr = remainingSeconds.toString().padStart(2, '0');

 if (hours > 0) {
 return `${hours}:${minutesStr}:${secondsStr}`;
 }
 return `${minutesStr}:${secondsStr}`;
}

// Function to safely sanitize text
function sanitizeText(text) {
 if (text === undefined || text === null) {
 return '';
 }
 return String(text)
 .replace(/&/g, '&amp;')
 .replace(/</g, '&lt;')
 .replace(/>/g, '&gt;')
 .replace(/"/g, '&quot;')
 .replace(/'/g, '&apos;');
}

// Generate the RSS feed header
let rssFeed = `<?xml version="1.0" encoding="UTF-8"?>
<rss xmlns:itunes="{{ $env.WEBHOOK_URL }}" 
 xmlns:content="{{ $env.WEBHOOK_URL }}"
 xmlns:atom="{{ $env.WEBHOOK_URL }}"
 version="2.0">
 <channel>
 <title>${sanitizeText(podcastTitle)}</title>
 <description>${sanitizeText(podcastDescription)}</description>
 <link>${sanitizeText(baseUrl)}</link>
 <atom:link href="${sanitizeText(webhookUrl)}" rel="self" type="application/rss+xml"/>
 <language>${sanitizeText(language)}</language>
 <copyright>© ${new Date().getFullYear()} ${sanitizeText(authorName)}</copyright>
 <lastBuildDate>${new Date().toUTCString()}</lastBuildDate>
 <itunes:author>${sanitizeText(authorName)}</itunes:author>
 <itunes:owner>
 <itunes:name>${sanitizeText(ownerName)}</itunes:name>
 <itunes:email>${sanitizeText(ownerEmail)}</itunes:email>
 </itunes:owner>
 <itunes:image href="${sanitizeText(coverImageUrl)}"/>
 <itunes:category text="${sanitizeText(itunesCategory)}"/>
 <itunes:explicit>${explicitContent}</itunes:explicit>
 <itunes:type>episodic</itunes:type>\n`;

// Generate items
for (const item of inputItems) {
 const json = item.json;

 // Extract values from the json object
 const title = sanitizeText(json.title);
 const description = sanitizeText(json.description);
 const link = sanitizeText(json.link);
 const date = json.date;
 const duration = json.duration;

 // Assign episode and season numbers dynamically based on row_number
 const episodeNumber = json.row_number; // Use row_number for the episode number
 const seasonNumber = 1; // You can adjust this logic if your episodes span multiple seasons

 rssFeed += ` <item>
 <title>${title}</title>
 <description>${description}</description>
 <link>${link}</link>
 <guid isPermaLink="false">${link}</guid>
 <pubDate>${formatDate(date)}</pubDate>
 <enclosure 
 url="${link}"
 length="0"
 type="audio/mpeg"/>
 <itunes:duration>${formatDuration(duration)}</itunes:duration>
 <itunes:summary>${description}</itunes:summary>
 <itunes:episodeType>full</itunes:episodeType>
 <itunes:episode>${episodeNumber}</itunes:episode>
 <itunes:season>${seasonNumber}</itunes:season>
 <itunes:explicit>${explicitContent}</itunes:explicit>
 <content:encoded>
 <![CDATA[
 <p>${description}</p>
 ]]>
 </content:encoded>
 </item>\n`;
}

// Close the RSS feed
rssFeed += ` </channel>
</rss>`;

// Return the complete RSS feed
return [{
 json: {
 rssFeed
 }
}];
