// Input: All items from the previous node
const input = $input.all();

// Step 1: Extract the PII column names from the first item
const firstItem = input[0];
if (!firstItem.json.data || !firstItem.json.data) {
 throw new Error("PII column names are missing in the input data.");
}
const piiColumns = firstItem.json.data.split(',').map(col => col.trim());
//console.log("PII Columns to Remove:", piiColumns);

// Step 2: Remove the first two items and process the remaining rows
let rows = input.slice(2).map(item => item.json); // Exclude the first item
//console.log("Rows to convert (before skipping last):", rows);


// Ensure there are rows to process
if (rows.length === 0) {
 throw new Error("No rows to convert to CSV.");
}

// Step 3: Remove PII columns from each row
const sanitizedRows = rows.map(row => {
 const sanitizedRow = { ...row }; // Copy the row
 piiColumns.forEach(column => delete sanitizedRow[column]); // Remove PII columns
 return sanitizedRow;
});
//console.log("Sanitized Rows:", sanitizedRows);

// Step 4: Extract headers from sanitized rows
const headers = Object.keys(sanitizedRows[0]); // Extract updated headers
//console.log("CSV Headers:", headers);

// Step 5: Convert rows to CSV format
const csvRows = [
 headers.join(','), // Add header row
 ...sanitizedRows.map(row => 
 headers.map(header => String(row[header] || '').replace(/,/g, '')).join(',') // Match headers with rows
 )
];

// Join all rows with a newline character
const csvContent = csvRows.join('\n');
//console.log("CSV Content:", csvContent);

const originalFileName = input[1].json.originalFilename;

// Step 7: Generate a new filename
const fileExtension = originalFileName.split('.').pop();
const baseName = originalFileName.replace(`.${fileExtension}`, '');
const newFileName = `${baseName}_PII_removed.${fileExtension}`;
//console.log("New Filename:", newFileName);

// Step 8: Return the CSV content and filename as JSON
return [
 {
 json: {
 fileName: newFileName, // New file name
 content: csvContent // CSV content as plain text
 }
 }
];
